import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Traditional Black Forest",
    description:
      "Whipped cream and cherries on top of a chocolate sponge cake that has been soaked in cherry syrup or kirsch.",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Belgian Chocolate Truffle",
    description: "A chocolate dreamland!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Chocolate Banana cake",
    description: "Richness of Banana with Coffee. ",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Biscoffi Cake",
    description: "Biscoff rich cake",
    price: 18.99,
  },
];

const AvailableMeals = () => {
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
